  <!-- Three.js + loaders from CDN (browser-only, free) -->
  <script type="module">
    import * as THREE from "https://cdn.jsdelivr.net/npm/three@0.160.1/build/three.module.js";
    import { GLTFLoader } from "https://cdn.jsdelivr.net/npm/three@0.160.1/examples/jsm/loaders/GLTFLoader.js";
    import { OrbitControls } from "https://cdn.jsdelivr.net/npm/three@0.160.1/examples/jsm/controls/OrbitControls.js";

    let scene, camera, renderer, controls, mixer, avatar;
    const clock = new THREE.Clock();
    let avatars = [];
    let activeAvatarId = null;

    function setStatus(text) {
      const el = document.getElementById("status");
      if (el) el.textContent = text;
    }

    // --- 3D AVATAR SETUP ---
    function init3D() {
      const container = document.getElementById("avatarCanvas");

      scene = new THREE.Scene();
      scene.background = new THREE.Color(0x020617);

      camera = new THREE.PerspectiveCamera(
        40,
        container.clientWidth / container.clientHeight,
        0.1,
        100
      );
      camera.position.set(0, 1.7, 3);

      renderer = new THREE.WebGLRenderer({ antialias: true });
      renderer.setPixelRatio(window.devicePixelRatio);
      renderer.setSize(container.clientWidth, container.clientHeight);
      renderer.outputEncoding = THREE.sRGBEncoding;
      renderer.shadowMap.enabled = true;
      container.appendChild(renderer.domElement);

      const hemi = new THREE.HemisphereLight(0xffffff, 0x111827, 1.0);
      scene.add(hemi);

      const dir = new THREE.DirectionalLight(0xffffff, 1.2);
      dir.position.set(2, 4, 3);
      dir.castShadow = true;
      dir.shadow.mapSize.set(1024, 1024);
      scene.add(dir);

      const groundGeo = new THREE.CircleGeometry(3, 64);
      const groundMat = new THREE.MeshStandardMaterial({
        color: 0x0b1120,
        roughness: 0.9,
        metalness: 0.0
      });
      const ground = new THREE.Mesh(groundGeo, groundMat);
      ground.rotation.x = -Math.PI / 2;
      ground.receiveShadow = true;
      scene.add(ground);

      controls = new OrbitControls(camera, renderer.domElement);
      controls.target.set(0, 1.4, 0);
      controls.enablePan = false;
      controls.minDistance = 2;
      controls.maxDistance = 4;

      window.addEventListener("resize", () => {
        camera.aspect = container.clientWidth / container.clientHeight;
        camera.updateProjectionMatrix();
        renderer.setSize(container.clientWidth, container.clientHeight);
      });

      animate();
    }

    function animate() {
      requestAnimationFrame(animate);
      const dt = clock.getDelta();

      if (mixer) mixer.update(dt);

      if (avatar) {
        const t = clock.elapsedTime;
        avatar.position.x = Math.sin(t * 0.4) * 0.15;
        avatar.position.y = 0.02 * Math.sin(t * 2.2);
      }

      if (renderer && scene && camera) {
        controls?.update();
        renderer.render(scene, camera);
      }
    }

    function loadAvatar(file, id) {
      if (!file) return;

      if (avatar) {
        scene.remove(avatar);
        avatar = null;
      }
      mixer = null;

      activeAvatarId = id;
      highlightActiveAvatar();

      setStatus("Loading avatar: " + file);

      const loader = new GLTFLoader();
      loader.load(
        file,
        (gltf) => {
          avatar = gltf.scene;
          avatar.traverse((obj) => {
            if (obj.isMesh) {
              obj.castShadow = true;
              obj.receiveShadow = true;
            }
          });

          avatar.position.set(0, 0, 0);
          avatar.rotation.y = Math.PI;

          scene.add(avatar);

          if (gltf.animations && gltf.animations.length > 0) {
            mixer = new THREE.AnimationMixer(avatar);
            const idle = gltf.animations[0];
            mixer.clipAction(idle).play();
          }

          setStatus("Avatar loaded successfully.");
        },
        undefined,
        (err) => {
          console.error("Avatar load error:", err);
          setStatus("Failed to load avatar file: " + file);
        }
      );
    }

    function highlightActiveAvatar() {
      document.querySelectorAll(".avatarItem").forEach((item) => {
        const id = item.getAttribute("data-id");
        if (id === activeAvatarId) item.classList.add("active");
        else item.classList.remove("active");
      });
    }

    // --- GALLERY / JSON LOADER ---
    async function loadAvatarList() {
      try {
        const res = await fetch("./avatars.json");
        avatars = await res.json();
      } catch (e) {
        console.error("avatars.json load error:", e);
        setStatus("Error loading avatars.json. Check file path.");
        return;
      }

      const list = document.getElementById("avatarList");
      list.innerHTML = "";

      avatars.forEach((av, idx) => {
        const item = document.createElement("div");
        item.className = "avatarItem";
        item.setAttribute("data-id", av.id);

        item.innerHTML = `
          <img src="${av.thumb}" alt="${av.name}" />
          <div class="avatarText">
            <div class="avatarName">${av.name}</div>
            <div class="avatarSource">${av.source || ""}</div>
          </div>
        `;

        item.addEventListener("click", () => {
          loadAvatar(av.file, av.id);
        });

        list.appendChild(item);

        if (idx === 0) {
          // Auto-load first avatar
          loadAvatar(av.file, av.id);
        }
      });
    }

    // --- WEBCAM (SHE "SEES" YOU) ---
    function initWebcam() {
      const video = document.getElementById("userCam");
      const btn = document.getElementById("btnCam");

      btn.addEventListener("click", async () => {
        try {
          const stream = await navigator.mediaDevices.getUserMedia({
            video: true,
            audio: false
          });
          video.srcObject = stream;
          setStatus("Camera enabled. She can see you.");
        } catch (err) {
          console.error("Camera error:", err);
          setStatus("Camera permission denied or not available.");
        }
      });
    }

    // --- HELPER: DOWNLOAD AVATAR FROM INTERNET URL ---
    function initDownloadHelper() {
      const urlInput = document.getElementById("avatarUrl");
      const nameInput = document.getElementById("avatarFileName");
      const btn = document.getElementById("btnDownloadAvatar");

      btn.addEventListener("click", async () => {
        const url = urlInput.value.trim();
        const name = (nameInput.value.trim() || "new_avatar").replace(/[^a-zA-Z0-9_-]/g, "_");

        if (!url) {
          setStatus("Paste a direct .glb file URL first.");
          return;
        }

        try {
          setStatus("Fetching avatar file from internet...");
          const res = await fetch(url);
          const blob = await res.blob();

          const a = document.createElement("a");
          a.href = URL.createObjectURL(blob);
          a.download = name + ".glb";
          a.style.display = "none";
          document.body.appendChild(a);
          a.click();
          document.body.removeChild(a);

          setStatus("Downloaded " + name + ".glb. Upload it into web/public/avatar/avatars and add an entry in avatars.json.");
        } catch (e) {
          console.error("Download error:", e);
          setStatus("Failed to download avatar from URL.");
        }
      });
    }

    window.addEventListener("DOMContentLoaded", () => {
      init3D();
      loadAvatarList();
      initWebcam();
      initDownloadHelper();
      setStatus("Avatar gallery loaded. Choose any avatar on the left.");
    });
  </script>
</head>

<body>
  <header>
    <span>Phuntroo ¬∑ Real Human Avatar Gallery (100% free, browser-only)</span>
    <span>Path: /web/public/avatar/index.html ¬∑ Served via GitHub Pages</span>
  </header>

  <div id="main">
    <!-- LEFT: AVATAR GALLERY -->
    <div id="gallery">
      <div id="galleryHeader">Avatars</div>
      <div id="avatarList"></div>
    </div>

      <!-- CENTER: 3D VIEWER -->
    <div id="viewer">
      <div id="avatarCanvas"></div>
      <div id="viewerOverlay">
        <span>Drag to orbit ¬∑ Scroll to zoom</span>
      </div>
    </div>

    <!-- RIGHT: CAMERA + STATUS + DOWNLOAD HELPER -->
    <div id="side">
      <div class="section">
        <div class="sectionTitle">Status</div>
        <div id="status">Initializing...</div>
      </div>

      <div class="section">
        <div class="sectionTitle">Camera</div>
        <button id="btnCam">Turn Camera ON</button>
        <video id="userCam" autoplay playsinline muted></video>
        <small>She does not send any video to a server. This is local in your browser only.</small>
      </div>

      <div class="section">
        <div class="sectionTitle">Add Avatar from Internet (Manual)</div>
        <small>1. Use any free AI/3D site that gives a direct <b>.glb</b> link (ReadyPlayerMe, Meshcapade, etc.)</small>
        <small>2. Paste that URL here and click <b>Download GLB</b>. The file will save to your computer.</small>
        <small>3. Upload that <b>.glb</b> into <code>web/public/avatar/avatars</code> and add entry into <code>avatars.json</code>.</small>

        <input id="avatarUrl" type="text" placeholder="https://.../real-human-avatar.glb" />
        <input id="avatarFileName" type="text" placeholder="local file name (no spaces), e.g. realhuman_3" />
        <button id="btnDownloadAvatar">Download GLB</button>
      </div>
    </div>
  </div>
</body>
</html>
```

This is a complete production-ready page:

* Reads `avatars.json` ‚Üí builds gallery dynamically
* Loads avatars via Three.js from your `/avatars/*.glb` files
* ‚ÄúAdd avatar from internet‚Äù helper to download `.glb` files locally so you can upload to GitHub

No backend, no GPU, no paid APIs.

---

## 5. End-to-end workflow to add a new AI-generated real human avatar

### Step 1 ‚Äì Generate a human face / body (free)

For example:

* Use **Artbreeder** / **PlaygroundAI** / **Lexica** to generate a realistic face image.
* Use **ReadyPlayerMe** to create a full body.

### Step 2 ‚Äì Convert to 3D `.glb`

Some options:

* **Meshcapade / VanceAI 3D face generator** ‚Üí upload face image ‚Üí export `.glb`
* For body + animations: use **Mixamo** ‚Üí download FBX ‚Üí use `fbxtogl.com` to convert to `.glb`.

Result: you have some `my_new_avatar.glb` file.

### Step 3 ‚Äì Upload files to GitHub

1. Go to:

   `phuntroo/web/public/avatar/avatars/`

2. Upload:

   * `my_new_avatar.glb`
   * `my_new_avatar.jpg` (thumbnail image of face/body)

### Step 4 ‚Äì Register avatar in `avatars.json`

Edit:

`phuntroo/web/public/avatar/avatars.json`

Add one more object to the JSON array, for example:

```json
{
  "id": "my_new_avatar",
  "name": "AI Human Face ‚Äì Variant 3",
  "file": "avatars/my_new_avatar.glb",
  "thumb": "avatars/my_new_avatar.jpg",
  "source": "PlaygroundAI + Meshcapade",
  "notes": "Ultra-realistic face generated by AI, converted to GLB."
}
```

Commit and push.

### Step 5 ‚Äì Open on GitHub Pages

After your usual deploy workflow:

```text
https://sudhir-bahadure.github.io/phuntroo/avatar/
```

* The new avatar appears in the left gallery list.
* Clicking it will load the `.glb` and show it in the 3D viewer.

---

## 6. How this integrates with your current project

* This page lives in `web/public/avatar/index.html`.
  Vite will copy it as-is into the built `/dist` output; it does not touch React routing.

* You still have your main app at `/` (React).
  This page is an extra ‚Äútool‚Äù page at `/avatar/`.

* No backend changes needed; no Node/Express changes; no paid keys.

---

If you want the **next step** later (for example: hook this gallery into your main React UI, or drive avatar choice from your Jarvis chat UI), I can give you a React component version that reads the same `avatars.json` and reuses the same logic.

